              aleDemanda[5]=dAleatorio6;
               aleDemanda[6]=dAleatorio7;
                aleDemanda[7]=dAleatorio8;
                 aleDemanda[8]=dAleatorio9;
           aleDemanda[9]=dAleatorio10;
           aleDemanda[10]=dAleatorio11;
           aleDemanda[11]=dAleatorio12;
            
            float rAleatorio1 =  (aleRetraso1.getText().isEmpty()== true ? -1 : Float.valueOf( aleRetraso1.getText()));
            float rAleatorio2 =  (aleRetraso2.getText().isEmpty()== true ? -1 : Float.valueOf( aleRetraso2.getText()));
            float rAleatorio3 =  (aleRetraso3.getText().isEmpty()== true ? -1 : Float.valueOf( aleRetraso3.getText()));
            float rAleatorio4 =  (aleRetraso4.getText().isEmpty()== true ? -1 : Float.valueOf( aleRetraso4.getText()));
            float rAleatorio5 =  (aleRetraso5.getText().isEmpty()== true ? -1 : Float.valueOf( aleRetraso5.getText()));
