    public Float encontrarRangoD(float value)
    {
        for(int i=0; i < 5 ; i++)
       {
           float comp= dfrecuenciA[i];
           if(value < comp)
           {
              return demanda[i];
           }
           
           
                   
       }
           return 0f;
           
    }
    
     public Float encontrarRangoR(float value)
    {
        for(int i=1; i < 5 ; i++)
       {
           float comp= rfrecuenciA[i];
           if(value < comp)
           {
              return demanda[i];
           }
           
           
                   
       }
           return 0f;
           
    }
    
